import java.nio.charset.StandardCharsets;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.Paths;
import java.security.KeyFactory;
import java.security.PublicKey;
import java.security.spec.X509EncodedKeySpec;
import java.security.MessageDigest;
import java.util.Base64;

import javax.crypto.Cipher;

public class Main {
    public static void main(String[] args) throws Exception {
        // Load the public key
        String publicKeyPath = "public_key.pem"; // Replace with your actual public key file path
        byte[] publicKeyBytes = Files.readAllBytes(Paths.get(publicKeyPath));
        X509EncodedKeySpec keySpec = new X509EncodedKeySpec(publicKeyBytes);
        KeyFactory keyFactory = KeyFactory.getInstance("RSA");
        PublicKey publicKey = keyFactory.generatePublic(keySpec);

        // Input string
        String inputString = "YourInputStringHere";

        // Step 1: Calculate SHA-512 hash
        MessageDigest sha512 = MessageDigest.getInstance("SHA-512");
        byte[] sha512Hash = sha512.digest(inputString.getBytes(StandardCharsets.UTF_8));

        // Step 2: Encrypt with RSA using PKCS1 padding
        Cipher rsaCipher = Cipher.getInstance("RSA/ECB/PKCS1Padding");
        rsaCipher.init(Cipher.ENCRYPT_MODE, publicKey);
        byte[] encryptedData = rsaCipher.doFinal(sha512Hash);

        // Step 3: Encode the encrypted result in Base64
        String base64Checksum = Base64.getEncoder().encodeToString(encryptedData);

        System.out.println("Encrypted Checksum: " + base64Checksum);
    }
}

